import { Container } from "react-bootstrap";

export default function Hero() {
  return (
    <div className="bg-light py-5 mb-4">
      <Container>
        <h1 className="display-5 fw-bold">Explore our simple, healthy recipes…</h1>
        <p className="lead">
          Discover easy-to-follow recipes designed for busy lifestyles — fresh, nutritious, and delicious.
        </p>
      </Container>
    </div>
  );
}
